        const context = ColorScale(width, height, colorscale);
        let rgba = context.getImageData(width * Math.min(meanPc, 0.99), 0, 1, 1).data;

        let color = `rgb(${rgba[0]}, ${rgba[1]}, ${rgba[2]})`;

